package Package;

public class MathsFunction {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char x='a',y='b';
		//unicodes for a=97,b=98 and so on
		int z;
		z= x+y;
		//z=97+98=195
		System.out.println(z);
		double a;
		//power
		a = Math.pow(3.0, 2.0);
		System.out.println(a);
		//Square root
		a = Math.sqrt(36);
		System.out.println(a);
		// log
		a = Math.log10(10);
		System.out.println(a);
		char ch = 'B',bh = 'B';
		int eh = ch+bh;
		System.out.println(eh);
	}
}
